import { StrictMode } from 'react'
import { createRoot } from 'react-dom/client'
import './index.css'
import App from './App.jsx'

// PWA Service Worker Registration with Vite PWA plugin
if ('serviceWorker' in navigator) {
  window.addEventListener('load', () => {
    // Use the service worker generated by Vite PWA plugin
    navigator.serviceWorker.register('/sw.js')
      .then((registration) => {
        console.log('âœ… Service Worker registered successfully:', registration)
        
        // Check for updates
        registration.addEventListener('updatefound', () => {
          const newWorker = registration.installing
          if (newWorker) {
            newWorker.addEventListener('statechange', () => {
              if (newWorker.state === 'installed' && navigator.serviceWorker.controller) {
                console.log('ðŸ”„ New version available')
                // The update notification component will handle showing the update prompt
              }
            })
          }
        })
      })
      .catch((registrationError) => {
        console.error('âŒ Service Worker registration failed:', registrationError)
      })
  })

  // Handle service worker updates
  navigator.serviceWorker.addEventListener('controllerchange', () => {
    console.log('ðŸ”„ Service Worker updated, reloading page...')
    window.location.reload()
  })
}

createRoot(document.getElementById('root')).render(
  <StrictMode>
    <App />
  </StrictMode>,
)
